          <form (submit)="signUp(userData)">
              <div class="form-group">
                <label for="name">Name : </label>
                <input type="text" name="name" class="form-control" placeholder="Name" [(ngModel)]="userData.name">
              </div>
              <div class="form-group">
                <label for="phone">Phone : </label>
                <input type="text" name="phone" class="form-control" placeholder="Phone" [(ngModel)]="userData.phone">
              </div>
              <div class="form-group">
                <label for="email">Email : </label>
                <input type="text" name="email" class="form-control" placeholder="Email" [(ngModel)]="userData.email">
              </div>
              <div class="form-group">
                <label for="password">Password : </label>
                <input type="password" name="password" class="form-control" placeholder="********" [(ngModel)]="userData.password">
              </div>
              <div class="form-group">
                <label for="c_password">Confirm Password : </label>
                <input type="password" name="c_password" class="form-control" placeholder="********" [(ngModel)]="userData.c_password">
              </div>
              <div class="text-center">
                <input type="submit" class="btn btn-my" value="Sign Up">
              </div>
              <div class="login">
                Already have an account ? <a routerLink="/login">Login</a> hera.
